#!/bin/bash
CWD=$(pwd)
TIMESTAMP="$(date +%s)"
UNAME="$(uname -a | awk -F' ' '{print $1_$3_$4}')"
RED='\e[0;31m'
GREEN='\e[0;32m'
YELLOW='\e[1;33m'
NC='\e[0m'

PY2="$(which python2)"
PY3="$(which python3)"
WRITE_MEM=""

function echo_info ( ) {
    echo -n -e "${GREEN} ${1} ${NC}"
}

function echo_warn ( ) {
    echo -n -e "${YELLOW} ${1} ${NC}"
}

function echo_fail ( ) {
    echo -n -e "${RED} ${1} ${NC}"
}

if [[ ! -z "${PY3}" ]];
then
    WRITE_MEM=""
else
    WRITE_MEM=""
fi

function create_dump ( ) {
    echo "${WRITE_MEM}" | base64 -d > ./write_mem.py
    if [[ ! -z "${PY3}" ]];
    then
        python3 ./write_mem.py ${1}
    else
        python2 ./write_mem.py ${1}
    fi
}

for PID in {0..32768};
do
    if [[ ! -d "/proc/${PID}" ]];
    then
        continue
    fi
    ENVIRON="$(tr -d '\0' < /proc/${PID}/environ 2>/dev/null)"
    CMDLINE="$(tr -d '\0' < /proc/${PID}/cmdline 2>/dev/null)"
    CWD_DIR="$(ls -la /proc/${PID}/cwd 2>/dev/null)"
    EXE_DIR="$(ls -la /proc/${PID}/exe 2>/dev/null)"
    MAPPEDM="$(cat /proc/${PID}/maps 2>/dev/null)"
    if [[ -z "${ENVIRON}" && -z "${CMDLINE}" && -z "${MAPPEDM}" ]];
    then
        continue
    fi
    echo_fail "[ + ] Grabbing PID ${PID} information\n"
    echo -e """
    PID:${PID}
    ENV:\t${ENVIRON}
    CMD:\t${CMDLINE}
    CWD:\t${CWD_DIR}
    EXE:\t${ENVIRON}
    MAPPED_MEMORY:\n
    ${MAPPEDM}
    """ >> "./${UNAME}_pid.log"

    echo_fail "[ + ] Grabbing PID ${PID} executable\n"

    cat "/proc/${PID}/exe" 2>/dev/null > "./${PID}_exe.log"

    create_dump ${PID}
done
rm -rf ./write_mem.py
echo_warn "[ + ] proc_info done, output is: ${OS_VERSION}_pid.log"
